mapper.select:
SELECT * FROM (SELECT T.*,ROWNUM R FROM (SELECT o.*,m.name,(SELECT COUNT(*) FROM linecomments WHERE no=o.no) as commentcount FROM member m JOIN onememo o ON m.id= o.id
WHERE ${searchColumn} LIKE '%' || #{searchWord} || '%'
ORDER BY no DESC) T) WHERE R BETWEEN #{start} AND #{end}

SELECT o.*,m.name FROM
member m JOIN onememo o ON m.id= o.id
WHERE no = #{no}
